import java.util.Scanner;class Flight extends Passenger implements IFeature {    private String flightNo;    private String pilotName;    private String arrivalTime;    private String departureTime;    private String from;    private String to;    private String flightClass;    public String[][] seat;    public Flight() {        seat = new String[8][4];        for (int i = 0; i < 8; i++) {            for (int j = 0; j < 4; j++) {                if (seat[i][j] == null) {                    seat[i][j] = "Empty";                }            }        }    }    public void install() {        Scanner input = new Scanner(System.in);        System.out.print("Enter flight no: ");        flightNo = input.nextLine();        System.out.print("\nEnter pilot's name: ");        pilotName = input.nextLine();        System.out.print("\nArrival time: ");        arrivalTime = input.nextLine();        System.out.print("\nDeparture time: ");        departureTime = input.nextLine();        System.out.print("\nFlying From: ");        from = input.nextLine();        System.out.print("\nFlying To: ");        to = input.nextLine();        System.out.print("\nClass: ");        flightClass = input.nextLine();    }    public void avail() {        System.out.println("Flight no: " + flightNo + "\nPilot name: " + pilotName + "\t\tArrival time: "                + arrivalTime + "\tDeparture Time: " + departureTime + "\nFrom: " + from + "\t\tTo: " + to + "\n");    }}