//1st divide it into columns and rows
public class Tes {

	public static void main(String[] args) {
		int rows = 9;
		for (int i = 1; i<=rows; i++)// decides Nth row
		{
			for (int j = 0; j<i; j++)//Decides how many columns 
			{
				System.out.print(i);//Not println 
			}
			System.out.println("");//Print a space which will end the  outer loop and restart it
		}
		// TODO Auto-generated method stub

	}

}
